import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'
import tailwindcss from '@tailwindcss/vite'

// https://vite.dev/config/
export default defineConfig({
  plugins: [react(), tailwindcss()],
})

import { StrictMode } from 'react'
import { createRoot } from 'react-dom/client'
import './index.css'
import App from './App.tsx'

async function enableMocking() {
  // 개발 환경에서만 MSW 실행
  if (import.meta.env.PROD) return

  const { worker } = await import('./mocks/browser')

  return worker.start({
    // MSW 핸들러에 등록되지 않은 요청은 실제 네트워크로 통과
    onUnhandledRequest: 'bypass',
  })
}

enableMocking().then(() => {
  createRoot(document.getElementById('root')!).render(
    <StrictMode>
      <App />
    </StrictMode>,
  )
})

import { setupWorker } from 'msw/browser'
import { handlers } from './handlers'

export const worker = setupWorker(...handlers)

import { http, HttpResponse } from 'msw'

function getDaysAgo(days: number): string {
  const date = new Date()
  date.setDate(date.getDate() - days)
  return date.toISOString()
}

export const handlers = [
  http.get('/api/v1/alerts/latest-result', () => {
    return HttpResponse.json({
      resultNo: 'RES-2026-001',
      checkupDate: getDaysAgo(10),
      patientName: '홍길동',
      patientId: 'PT-10042',
      testType: '종합건강검진',
      doctorName: '김철수 원장',
      // CTA 버튼용 링크 (과제 요구사항 반영: 2개 버튼)
      aiCoachUrl: 'https://example.com/ai-coach',
      resultReportUrl: 'https://example.com/results/RES-2026-001',
    })
  }),
]
